import posts from '../../posts.json'
import moment from 'moment'

const Post = props => {
    return (
        <div>
            <h1>{props.post.title}</h1>
            <p>Published on {moment().format('dddd D MMMM YYYY')}</p>
            <p>{props.post.content}</p>
        </div>
    )
}

Post.getInitialProps = ({ query }) => {
    return {
        post: posts[query.id]
    }
}

export default Post
